import { useDispatch } from "react-redux"

const LoginFormPage = () => {
    const dispatch = useDispatch()
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleEmail = (e) => {
        e.preventDefault()
        setEmail(e.currentTarget.value)
    }

    const handlePassword = (e) => {
        e.preventDefault()
        setPassword(e.currentTarget.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(loginUser(User.find_by_credentials(email, password)))
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <label id='LoginEmailLabel'>Email
                    <input
                        type="text" value={email} onChange={handleEmail}
                    ></input>
                </label>
                <label id='LoginPasswordLabel'>Password 
                    <input
                        type="password" value={password} onChange={handlePassword}
                    ></input>
                </label>
                <button type="submit" value="Log In" id='LogInButton'>Log In</button>
            </form>
        </>
    )
}

export default LoginFormPage
